import React, { Component } from 'react';
import axios from 'axios';

export default class ProductList extends Component {

    constructor(props) {
	super(props);
	axios.get('http://localhost:4000/consortium/products')
	    .then(res => {
		this.state = {productsToBuy: res.data};
		console.log(this.state.productsToBuy);

		let rows = [];
		for (var i = 0; i < this.state.productsToBuy.length; i++){
		    let rowID = `row${i}`
		    let cell = []
		    for (var idx = 0; idx < this.state.size; idx++){
			let cellID = `cell${i}-${idx}`
			cell.push(<td key={cellID} id={cellID}></td>)
		    }
		    rows.push(<tr key={i} id={rowID}>{cell}</tr>)
		}
	    })
	    .catch(function (error){
                console.log(error);
	    })
    }

    getTableBodyAsReactElement() {
	const inv = !this.state ? null : this.state.productsToBuy;
	console.log('inv: ', inv);

	return (!inv) ? null : (
	    <tbody>
              {inv.map((item) => {                              
					console.log('item: ', item);
					return (
					    <tr>
					      {Object.entries(item).map((field) => {
						  console.log('field: ', field);
						  return <td>{field[1]}</td>
  })}
</tr>
					);
              })}
	    </tbody>
	);
    }

    render() {
        return (
	    <table border="1">
              {this.getTableBodyAsReactElement()}
	    </table>
        )
    }
}
