import React, { Component } from 'react';
import axios from 'axios';

import Product from "./product";

export default class SellProduct extends Component {

    state = {
	productsOnSale: [],
	productId: "",
	name: "",
	price: "",
	description: ""
    };

    componentDidMount() {
	axios.get('http://localhost:4000/consortium/products')
	    .then(res => {
		this.setState({productsOnSale: res.data});
	    })
	    .catch(function (error){
                console.log(error);
	    })	
    }

    listProducts = (username, page) => {
        return this.state.productsOnSale.map(function(currentProduct, i){
	    if(username === currentProduct.owner) {
		return <Product
		page={page}
		product={currentProduct}
		key={i} />;
	    }
        })
    }

    handleProductId = (text) => {
	this.setState({productId: text.target.value})
    }

    handleName = (text) => {
	this.setState({name: text.target.value})
    }

    handlePrice = (text) => {
	this.setState({price: text.target.value})
    }

    handleDescription = (text) => {
	this.setState({description: text.target.value})
    }

    sell = (username) => {
	let obj = {};
	obj.productId = this.state.productId;
	obj.name = this.state.name;
	obj.price = this.state.price;
	obj.description = this.state.description;
	obj.owner = username;
	let products = this.state.productsOnSale;

	axios({
	    method: 'post',
	    url: 'http://localhost:4000/consortium/sell',
	    data: obj,
	    config: { headers: {'Content-Type': 'application/json' }}
	})
	    .then(res => {
		products.push(obj);
		this.setState({productsOnSale: products});
	    })
	    .catch(function (error){
                console.log(error);
	    })	
    }

    render() {
        return (
	    <div>
              <h3>Your products on sale</h3>
              <table className="table table-striped" style={{ marginTop: 20 }} >
                <thead>
                  <tr>
		    <th>ID</th>
                    <th>Name</th>
                    <th>Price</th>
                    <th>Description</th>
                    <th>Seller</th>
                  </tr>
                </thead>
                <tbody>
                  { this.listProducts(this.props.props.username, this.props.props.page) }
                </tbody>
              </table>

	      <div className="form-inline text-center">
		<div className="col-md-12 form-group">
		  <label htmlFor="productId" className="col-form-label">ProductID: </label>
		  <input type="number" placeholder="Enter productId: " id="productId" className="form-control" onChange={(text) => this.handleProductId(text)} />
		</div>
		<div className="col-md-12 form-group">
		  <label htmlFor="name" className="col-form-label">Name: </label>
		  <input type="text" placeholder="Enter name: " id="name" className="form-control" onChange={(text) => this.handleName(text)} />
		</div>
		<div className="col-md-12 form-group">
		  <label htmlFor="price" className="col-form-label">Price: </label>
		  <input type="number" placeholder="Enter price: " id="price" className="form-control" onChange={(text) => this.handlePrice(text)} />
		</div>
		<div className="col-md-12 form-group">
		  <label htmlFor="description" className="col-form-label">Description: </label>
		  <input type="text" placeholder="Enter description: " id="description" className="form-control" onChange={(text) => this.handleDescription(text)} />
		</div>
		<div className="col-md-12 form-group">
		  <button className="btn btn-primary btn-sm m-2" onClick={() => this.sell(this.props.props.username)}>Sell</button>
		</div>
              </div>

            </div>
        )
    }
}
