import React, { Component } from 'react';
import axios from 'axios';

import Product from "./product";

export default class ProductList extends Component {

    constructor(props) {
	super(props);
	this.state = {productsToBuy: []};
    }

    componentDidMount() {
	axios.get('http://localhost:4000/consortium/products')
	    .then(res => {
		this.setState({productsToBuy: res.data});
	    })
	    .catch(function (error){
                console.log(error);
	    })	
    }

    listProducts() {
        return this.state.productsToBuy.map(function(currentProduct, i){
	    if(this.props.username !== currentProduct.owner) {
		return <Product product={currentProduct} key={i} />;
	    }
        })
    }

    render() {
        return (
	    <div>
              <h3>Products List</h3>
              <table className="table table-striped" style={{ marginTop: 20 }} >
                <thead>
                  <tr>
                    <th>Name</th>
                    <th>Price</th>
                    <th>Description</th>
                    <th>Owner</th>
                  </tr>
                </thead>
                <tbody>
                  { this.listProducts() }
                </tbody>
              </table>
            </div>
        )
    }
}
