import React, { Component } from 'react';

import "bootstrap/dist/css/bootstrap.min.css";

class NavBar extends Component {
    state = {
	displayMenu: false
    };

    showDropDownMenu() {
	console.log("here");
	this.setState({ displayMenu: true }, () => {
	    document.addEventListener('click', this.hideDropdownMenu);
	});
    }

    hideDropdownMenu() {
	this.setState({ displayMenu: false }, () => {
	    document.removeEventListener('click', this.hideDropdownMenu);
	});	
    }

    render() {
	return (
	    <nav className="navbar navbar-expand-lg navbar-light bg-light">
	      <a className="navbar-brand" href="#">Consortium</a>
	      <div className="collapse navbar-collapse">
		<ul className="navbar-nav nav mr-auto">
		  <li className="navbar-item active">
		    <a className="nav-link" href="#">Buy</a>
		  </li>
		  <li className="navbar-item">
		    <a className="nav-link" href="#">Sell</a>
		  </li>
		</ul>
		<div className="navbar-nav nav ml-auto dropdown">

		  <button className="nav-link dropdown-toggle" onClick={this.showDropdownMenu}>{this.props.user}</button>
		      { this.state.displayMenu ? (
			  <ul>
			    <li><a>Logout</a></li>
			  </ul>
		      ) : (null)}


		</div>
	      </div>
	    </nav>
	);
    }
};

export default NavBar;
