import React, { Component } from "react";

import "bootstrap/dist/css/bootstrap.min.css";

import Login from "./components/login";
import ProductList from "./components/productList";
import SellProduct from "./components/sellProduct";
import NavBar from "./components/navbar";

import axios from 'axios';

class App extends Component {

    state = {
	page: "Login",
	username: "",
	password: ""
    };

    handleUsername = (text) => {
	this.setState({username: text.target.value})
    }

    handlePassword = (text) => {
	this.setState({password: text.target.value})
    }

    handleLogin = () => {
	let obj = {};
	obj.username = this.state.username;
	obj.password = this.state.password;


	axios({
	    method: 'post',
	    url: 'http://localhost:4000/consortium/login',
	    data: obj,
	    config: { headers: {'Content-Type': 'application/json' }}
	})
	    .then(res => {
		console.log(res.data);
		this.setState({page: "Main"});
	    })
	    .catch(function (error){
                console.log(error);
	    })
    }

    handlePageChange = (page) => {
	const {state} = this.state;
	state.page = page;
	this.setState({state});
    }

    render() {	
	return (
		<main className="container">
		{this.state.page === "Login" ? (<Login
						props={this.state}
						onUsernameChange={this.handleUsername}
						onPasswordChange={this.handlePassword}
						onLogin={this.handleLogin}
						/>) : null}
	    {this.state.page !== "Login" ? (<NavBar />) : null}
	    {this.state.page !== "Login" ? (<ProductList />) : null}
	    {this.state.page !== "Login" ? (<SellProduct />) : null}
	    </main>
	);
    }
}

export default App;
