import React, { Component } from 'react';
import axios from 'axios';
import "bootstrap/dist/css/bootstrap.min.css";

export default class Login extends Component {
    state = {
	username: "",
	password: ""
    };

    handleUsername(text) {
	this.setState({username: text.target.value})
    }

    handlePassword(text) {
	this.setState({password: text.target.value})
    }

    loginRegister(value) {
	let obj = {};
	obj.username = this.state.username;
	obj.password = this.state.password;

	if(value === "login") {
	    axios({
		method: 'post',
		url: 'http://localhost:4000/consortium/login',
		data: obj,
		config: { headers: {'Content-Type': 'application/json' }}
	    })
		.then(res => console.log(res.data))
		.catch(function (error){
                    console.log(error);
		})
	}
	else if(value === "register") {
	    axios({
		method: 'post',
		url: 'http://localhost:4000/consortium/register_user',
		data: obj,
		config: { headers: {'Content-Type': 'application/json' }}
	    })
		.then(res => console.log(res.data))
		.catch(function (error){
                    console.log(error);
		})
	}
    }

    render() {
        return (
            <div className="form-inline text-center">
	      <div className="col-md-12 form-group">
		<label htmlFor="username" className="col-sm-2 col-form-label">Username: </label>
		<input type="text" placeholder="Enter username" id="username" className="form-control" onChange={(text) => {this.handleUsername(text)}} />
	      </div>
	      <div className="col-md-12 form-group">
		<label htmlFor="password" className="col-sm-2 col-form-label">Password: </label>
		<input type="password" placeholder="Enter password" id="password" className="form-control" onChange={(text) => {this.handlePassword(text)}} />
	      </div>
	      <div className="col-md-12 form-group">
		<button className="btn btn-primary btn-sm m-2" value="login" onClick={(value) => {this.loginRegister(value)}}>Login</button>
		<button className="btn btn-primary btn-sm m-2" value="register" onClick={(value) => {this.loginRegister(value)}}>Register</button>
	      </div>
            </div>
        )
    }
}
