import React, { Component } from 'react';

import "bootstrap/dist/css/bootstrap.min.css";

class NavBar extends Component {
    state = {
	displayMenu: false
    };

    showDropDownMenu = (event) => {
	event.preventDefault();
	this.setState({ displayMenu: true }, () => {
	    document.addEventListener('click', this.hideDropdownMenu);
	});
    }

    hideDropdownMenu = () => {
	this.setState({ displayMenu: false }, () => {
	    document.removeEventListener('click', this.hideDropdownMenu);
	});	
    }

    render() {
	return (
	    <nav className="navbar navbar-expand-lg navbar-light bg-light">
	      <a className="navbar-brand" href="#">Consortium</a>
	      <div className="collapse navbar-collapse">
		<ul className="navbar-nav nav mr-auto">
		  <li className="navbar-item active">
		    <a className="nav-link" href="#">Buy</a>
		  </li>
		  <li className="navbar-item">
		    <a className="nav-link" href="#">Sell</a>
		  </li>
		</ul>
		<ul className="navbar-nav nav ml-auto">
		  <li className="nav-item dropdown">
		    <a className="nav-link dropdown-toggle">{this.props.user}</a>
		    <div className="dropdown-menu">
		      <a className="nav-item nav-link dropdown-toggle">
			<span className="caret" onClick={this.showDropdownMenu}></span>
		      </a>
		      { this.state.displayMenu ? (
			  <ul>
			    <li><a className="dropdown-item">Logout</a></li>
			  </ul>
		      ) : (null)}
		    </div>
		  </li>	      
		</ul>
	      </div>
	    </nav>
	);
    }
};

export default NavBar;
