import React, { Component } from 'react';
import axios from 'axios';

import Product from "./product";
import Transaction from "./transaction";

export default class ProductList extends Component {

    state = {
	productsToBuy: [],
	transactions: [],
	deposit: 0
    };

    componentDidMount() {
	axios.get('http://localhost:4000/consortium/products')
	    .then(products => {
		this.setState({productsToBuy: products.data});

		axios.get('http://localhost:4000/consortium/transactions')
		    .then(trans => {
			this.setState({transactions: trans.data});
		    })
		    .catch(function (error){
			console.log(error);
		    })		
	    })
	    .catch(function (error){
                console.log(error);
	    })	
    }

    buyProduct = (product, username) => {
	let obj = {};
	obj.username = username;
	obj.productId = product.productId;
	obj.name = product.name;
	obj.price = product.price;
	obj.description = product.description;
	obj.owner = product.owner;

	axios({
	    method: 'post',
	    url: 'http://localhost:4000/consortium/buy',
	    data: obj,
	    config: { headers: {'Content-Type': 'application/json' }}
	})
	    .then(res => {
		this.setState(prevState => {
		    const productsToBuy = prevState.productsToBuy.filter(p => p.productId !== product.productId);
		    return { productsToBuy };
		});
	    })
	    .catch(function (error){
                console.log(error);
	    })	
    }

    listProducts = (username, page, buyProduct) => {
        return this.state.productsToBuy.map(function(currentProduct, i){
	    if(username !== currentProduct.owner) {
		return <Product
		page={page}
		product={currentProduct}
		username={username}
		key={i}
		buyProduct={buyProduct} />;
	    }
        })
    }

    handleDeposit = (text) => {
	this.setState({deposit: text.target.value})
    }

    settlePayment = (transaction) => {
	let obj = {};
	obj.buyer = transaction.buyer;
	obj.seller = transaction.seller;
	obj.amount = transaction.amount;
	obj.productId = transaction.productId;

	axios({
	    method: 'post',
	    url: 'http://localhost:4000/consortium/settle_payment',
	    data: obj,
	    config: { headers: {'Content-Type': 'application/json' }}
	})
	    .then(res => {
		let t = [...this.state.transactions];
		var index = t.indexOf(transaction)
		if (index !== -1) {
		    t.splice(index, 1);
		    this.setState({transactions: t});
		}
	    })
	    .catch(function (error){
                console.log(error);
	    })	
    }

    listTransactions = (username, settlePayment) => {
        return this.state.transactions.map(function(currentTransaction, i){
	    if(username === currentTransaction.buyer) {
		return <Transaction
		transaction={currentTransaction}
		key={i}
		settlePayment={settlePayment} />;
	    }
        })
    }

    render() {
        return (
	    <div>
              <h3>Products to buy</h3>
              <table className="table table-striped" style={{ marginTop: 20 }} >
                <thead>
                  <tr>
		    <th>ID</th>
                    <th>Name</th>
                    <th>Price</th>
                    <th>Description</th>
                    <th>Seller</th>
                  </tr>
                </thead>
                <tbody>
                  { this.listProducts(this.props.props.username, this.props.props.page, this.buyProduct) }
                </tbody>
              </table>

	      <div className="form-inline">
	      <label htmlFor="deposit" className="col-form-label">Deposit: </label>
	      <input type="number" placeholder="Enter deposit: " id="deposit" className="form-control" onChange={(text) => this.handleDeposit(text)} />
		<button className="btn btn-primary btn-sm m-2" onClick={() => this.props.depositMoney(this.props.props.username, this.state.deposit)}>Deposit</button>
	      </div>

	      <h3>Transactions to settle</h3>
              <table className="table table-striped" style={{ marginTop: 20 }} >
                <thead>
                  <tr>
		    <th></th>
                    <th>Creditor</th>
                    <th>Amount</th>
                    <th>Product</th>
                  </tr>
                </thead>
                <tbody>
                  { this.listTransactions(this.props.props.username, this.settlePayment) }
                </tbody>
              </table>

            </div>
        )
    }
}
