import React, { Component } from 'react';
import axios from 'axios';

export default class Login extends Component {
    state = {
	username: "",
	password: ""
    };

    handleUsername(text) {
	this.setState({username: text.target.value})
    }

    handlePassword(text) {
	this.setState({password: text.target.value})
    }

    login() {
	let obj = {};
	obj.username = this.state.username;
	obj.password = this.state.password;

	axios({
	    method: 'post',
	    url: 'http://localhost:4000/consortium/login',
	    data: obj,
	    config: { headers: {'Content-Type': 'application/json' }}
	})
            .then(res => console.log(res.data))
	    .catch(function (error){
                console.log(error);
            })
    }

    render() {
        return (
            <div>
	      <input type="text" placeholder="Enter username" onClick={(text) => {this.handleUsername(text)}} /> <br />
		<input type="text" placeholder="Enter password" onClick={(text) => {this.handlePassword(text)}} /> <br />
		  <button onClick={() => {this.login()}}>Login</button>

            </div>
        )
    }
}
